mapper.select:
select id, content, created_date, updated_date
from report
where created_date between #{start} and #{end}
order by updated_date desc;

select id, content, created_date, updated_date
from report
where id = #{id}

mapper.update:
update report
set content = #{content}, updated_date = #{updatedDate}
where id = #{id}

update report
set updated_date = #{updatedDate}
where id = #{id}
